/*
 * whenever you have 'multiple condition checking' you can use switch case statement 
 * in place of if else
 * 
 */

package Java.demo;

public class SwitchOps {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int choice=1;
	switch(choice)
	{
	case 1:
		System.out.println("selected case is 1");
		break;
	case 2:
		System.out.println("selected case is 2");
		break;
	default:
		System.out.println("no match found");
		
	}

}
}
